'CREATE OR REPLACE EDITIONABLE FUNCTION "FIND_OLDEST_UCZESTNIK" ',
'RETURN VARCHAR2',
'AS',
'  oldest_age INTEGER;',
'  oldest_name VARCHAR2(32);',
'  wynik varchar2(200);',
'BEGIN',
'  SELECT wiek, imie',
'  INTO oldest_age, oldest_name ',
'  FROM uczestnicy',
'  JOIN zam',
'  ON uczestnicy.id_klienta = zam.id_klienta',
'  order by wiek DESC FETCH FIRST 1 rows only;',
'  wynik:= ''Najstarszy uczestnik to: '' || oldest_name || '' ma '' || oldest_age || '' lat.'';',
'  RETURN wynik;',
'END;',
